import React,{useState, useRef} from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faPlay} from "@fortawesome/free-solid-svg-icons";
import {faAngleRight} from "@fortawesome/free-solid-svg-icons";
import {faAngleLeft} from "@fortawesome/free-solid-svg-icons";


const Player = ({currentSong, isPlaying, setIsPlaying}) => {
    const audio = useRef(null)


    function handlerClick(){
        console.log(audio)
        let audio = document.getElementById('music')
        setIsPlaying(!isPlaying)
        if(isPlaying == true){
            audio.pause()
        }else{
            audio.play()
        }
    }
    return(
        <div className='playerContainer'>
            <div className='playerFoot'>
                <p>start time:</p>
                <input className='range' type="range"/>
                <p>end time:</p>
            </div>
            <div className='playerArrows'>
                <FontAwesomeIcon  className='icon1 icon' size='2x' icon={faAngleLeft} />
                <FontAwesomeIcon onClick = {handlerClick} className='icon2 icon' size='3x' icon={faPlay}  />
                <FontAwesomeIcon className='icon3 icon' size='2x' icon={faAngleRight} />
            </div>
            <audio ref={audio} src={currentSong.audio}></audio>
        </div>
    )
}

export default Player
