import React,{useState, useRef} from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faPlay} from "@fortawesome/free-solid-svg-icons";
import {faAngleRight} from "@fortawesome/free-solid-svg-icons";
import {faAngleLeft} from "@fortawesome/free-solid-svg-icons";
import {faPause} from "@fortawesome/free-solid-svg-icons";


const Player = ({currentSong, isPlaying, setIsPlaying}) => {
    const audio = useRef(null)
    const [time, setTime] = useState({
        current: 0,
        duration: 0
    })

    function handlerClick(){
        setIsPlaying(!isPlaying)
        if(isPlaying === true){
            audio.current.pause()
        }else{
            audio.current.play()
        }
    }
    function handlerTime(){
        console.log(audio.current.currentTime)
        console.log(audio.current.duration)
    }
    return(
        <div className='playerContainer'>
            <div className='playerFoot'>
                <p>start time:</p>
                <input className='range'
                       type="range"
                min={0}
                max={audio.current.duration}
                value={audio.current.duration}/>
                <p>end time:</p>
            </div>
            <div className='playerArrows'>
                <FontAwesomeIcon  className='icon1 icon' size='2x' icon={faAngleLeft} />
                <FontAwesomeIcon onClick = {handlerClick} className='icon2 icon' size='3x' icon={isPlaying ? faPause : faPlay}  />
                <FontAwesomeIcon className='icon3 icon' size='2x' icon={faAngleRight} />
            </div>
            <audio onTimeUpdate={handlerTime} ref={audio} src={currentSong.audio}></audio>

        </div>
    )
}

export default Player
